$(document).ready(function() {

	var action = 1,
	contNavHeight = 0;


	if ($(".content-wrap .nav-content").length){
		contNavHeight = $(".content-wrap .nav-content").height();
	}

    $('[data-toggle="tooltip"]').tooltip(), $(".box-switch").click(function() {
        $("#course_details").toggle(),$(".courses-tab").css("height","initial"), $(".content-wrap").css("top", 50 + 30 + $(".courses-tab").height()+ contNavHeight + "px" ), $(".content-wrap .nav-content").css("top", 50 + 30 + $(".courses-tab").height() + "px") , $(".right-wrap").css("height", "calc(100% - " + (50 + 30 + $(".courses-tab").height()) + "px)"), $(".right-wrap").css("top", 50 + 30 + $(".courses-tab").height() + "px");


        if ( action == 1 ) {
	        $(".btn.box-switch").html('Show Menu <i class="icon icon-down-open"></i>');
	        action = 2;
	    } else {
	        $(".btn.box-switch").html('Hide Menu <i class="icon icon-up-open"></i>');
	        action = 1;
	    }
    })
});

// search clear option
    $(document).ready(function(){
        $(".srch_txt").keyup(function(){
                $("#searchclear").toggle(Boolean($(this).val()));
        });
        $("#searchclear").toggle(Boolean($(".srch_txt").val()));
        $("#searchclear").click(function(){
        $(".srch_txt").val('').focus();
        $(this).hide();
        });
    });
// ends