SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
SET time_zone = "+00:00";



CREATE TABLE IF NOT EXISTS `accounts` (
  `id` int(11) NOT NULL,
  `acct_name` varchar(255) NOT NULL,
  `acct_domain` varchar(255) NOT NULL,
  `acct_domain_whitelist` text,
  `acct_host` varchar(255) NOT NULL DEFAULT 'localhost',
  `acct_database` varchar(255) NOT NULL DEFAULT 'ofabee_version_3',
  `acct_db_user` varchar(255) NOT NULL DEFAULT 'ofabeeApacheUser',
  `acct_db_password` varchar(255) NOT NULL DEFAULT 'fiS4j6*9',
  `acct_status` enum('1','0') NOT NULL DEFAULT '0',
  `acct_deleted` enum('1','0') NOT NULL DEFAULT '0',
  `action_id` int(11) NOT NULL,
  `action_by` int(11) NOT NULL,
  `acct_expiry_date` date NOT NULL,
  `acct_environment` varchar(50) NOT NULL DEFAULT 'production',
  `acct_colour` longtext NOT NULL,
  `created_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_date` datetime NOT NULL DEFAULT '2018-11-09 00:00:00'
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

CREATE TABLE IF NOT EXISTS `account_settings` (
  `id` int(11) NOT NULL,
  `as_account_id` int(11) NOT NULL,
  `as_key_id` int(11) NOT NULL,
  `as_superadmin_value` int(11) NOT NULL DEFAULT '0',
  `as_siteadmin_value` int(11) NOT NULL DEFAULT '0',
  `as_setting_value` longtext NOT NULL,
  `action_id` int(11) NOT NULL,
  `action_by` int(11) NOT NULL,
  `created_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_date` datetime DEFAULT NULL
) ENGINE=InnoDB AUTO_INCREMENT=2731 DEFAULT CHARSET=latin1;


CREATE TABLE IF NOT EXISTS `settings_keys` (
  `id` int(11) NOT NULL,
  `sk_key` varchar(255) DEFAULT NULL,
  `sk_value_format` text,
  `sk_has_dropdown` enum('0','1') NOT NULL DEFAULT '1',
  `sk_super_admin` tinyint(1) NOT NULL DEFAULT '0',
  `sk_site_admin` tinyint(1) NOT NULL DEFAULT '0',
  `created_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_date` datetime DEFAULT NULL
) ENGINE=InnoDB AUTO_INCREMENT=61 DEFAULT CHARSET=latin1;

